class FirebaseDetailScreenDataSource {
  final Firestore _fStore;

  final BehaviorSubject<List<Details>> _detailScreenSubject = BehaviorSubject();
  Stream<List<Details>> get outDetailScreen => _detailScreenSubject.stream;

  FirebaseDetailScreenDataSource(this._fStore) {
    //if you ever want to clear data uncomment the line below
    SharedPreferences.getInstance().then((prefs) {
//      prefs.setStringList('kapitel_details', null);
      if (prefs.getStringList('kapitel_details') == null) {
        _detailScreenSubject.sink.add(details);
        prefs.setString('kapitel_details',
            json.encode(details.map((e) => e.toJson()).toList()));
      }

      _fStore
          .collection("kapital_details")
          .getDocuments()
          .then((querySnapshot) {
        var detailsRemote = querySnapshot.documents
            .map((detailDoc) => Details.fromJson(detailDoc.data))
            .toList()
            .cast<Details>();
        var detailsLocal = prefs
            .getStringList('kapitel_details')
            .map((e) => Details.fromJson(json.decode(e)))
            .toList()
            .cast<Details>();
        var detailz = detailsRemote
            .map((e) => e.update(
                detailsLocal.where((element) => element.id == e.id).first))
            .toList()
            .cast<Details>();
        _detailScreenSubject.sink.add(detailz);
        prefs.setStringList('kapitel_details',
            detailz.map((e) => json.encode(e.toJson())).toList());
      });
    });
  }

  List<Details> getDetails(String themengebietId) {
    return _detailScreenSubject.value
        .where((element) => element.themengebietId == themengebietId)
        .toList()
        .cast<Details>();
  }

  List<Details> getBookMarkedDetails() {
    return _detailScreenSubject.value
        ?.where((element) => element.isBookMarked == true)
        ?.toList()
        ?.cast<Details>();
  }

  bookMark(Details detail) {
    SharedPreferences.getInstance().then((prefs) {
      List<Details> details;
      details = _detailScreenSubject.value;
      details.where((element) => element.id == detail.id).first.isBookMarked =
          !(details
                  .where((element) => element.id == detail.id)
                  .first
                  .isBookMarked ??
              false);
      _detailScreenSubject.sink.add(details);
      prefs.setStringList('kapitel_details',
          details.map((e) => json.encode(e.toJson())).toList());
    });
  }

  seen(Details detail) {
    SharedPreferences.getInstance().then((prefs) {
      List<Details> details;
      details = _detailScreenSubject.value;
      details.where((element) => element.id == detail.id).first.isSeen =
          !(details.where((element) => element.id == detail.id).first.isSeen ??
              false);
      _detailScreenSubject.sink.add(details);
      prefs.setStringList('kapitel_details',
          details.map((e) => json.encode(e.toJson())).toList());
    });
  }

  justSaw(Details detail) {
    SharedPreferences.getInstance().then((prefs) {
      List<Details> details;
      details = _detailScreenSubject.value;
      details.where((element) => element.id == detail.id).first.isSeen = true;
      details.where((element) => element.id == detail.id).first.lastSeen =
          DateTime.now();
      _detailScreenSubject.sink.add(details);
      prefs.setStringList('kapitel_details',
          details.map((e) => json.encode(e.toJson())).toList());
    });
  }

  void dispose() {
    _detailScreenSubject.drain();
  }

